import React, { useState, useEffect } from 'react';
import { GlowingButton, WaveAnimation } from './CreativeElements';
import './Home.css';

const Home = () => {
  const [timeLeft, setTimeLeft] = useState({});
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 1,
      title: "UPHARMORA - 1.0",
      subtitle: "NATIONAL CONFERENCE 2025",
      content: "Join us for an inspiring gathering of minds, innovation, and academic excellence in pharmaceutical sciences",
      theme: "Fostering Innovation for a Healthier Tomorrow: Navigating the Future of Pharmaceutical Sciences",
      showCountdown: true
    },
    {
      id: 2,
      title: "UNITED UNIVERSITY",
      subtitle: "FACULTY OF PHARMACY",
      content: "Established in 2023, offering B.Pharm and Diploma in Pharmacy programs with state-of-the-art facilities and highly qualified faculty",
      theme: "Excellence in Pharmaceutical Education",
      showCountdown: false
    },
    {
      id: 3,
      title: "CONFERENCE HIGHLIGHTS",
      subtitle: "NOVEMBER 7-8, 2025",
      content: "Keynote speakers, research presentations, networking opportunities, and cutting-edge pharmaceutical innovations",
      theme: "Two Days of Knowledge & Innovation",
      showCountdown: false
    },
    {
      id: 4,
      title: "PRAYAGRAJ",
      subtitle: "THE SANGAM CITY",
      content: "Experience the rich cultural heritage and spiritual significance of one of India's most ancient cities",
      theme: "Where Tradition Meets Innovation",
      showCountdown: false
    }
  ];

  useEffect(() => {
    const conferenceDate = new Date('2025-11-07T09:00:00').getTime();

    const timer = setInterval(() => {
      const now = new Date().getTime();
      const difference = conferenceDate - now;

      if (difference > 0) {
        setTimeLeft({
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((difference % (1000 * 60)) / 1000)
        });
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(slideTimer);
  }, [slides.length]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <section id="home" className="home">
      <div className="slideshow-container">
        {slides.map((slide, index) => (
          <div 
            key={slide.id} 
            className={`slide ${index === currentSlide ? 'active' : ''}`}
          >
            <div className="slide-content">
              <div className="slide-header">
                <h1>
                  {slide.id === 1 ? (
                    <><span className="u-red">U</span><span className="pharmora-blue">PHARMORA - 1.0</span></>
                  ) : (
                    slide.title
                  )}
                </h1>
                {slide.id === 1 && <div className="title-underline"></div>}
              </div>

              <h2 className="slide-subtitle">{slide.subtitle}</h2>

              {slide.id === 1 && (
                <>
                  <div className="year-divider">
                    <span className="year-line"></span>
                    <span className="year-text">2025</span>
                    <span className="year-line"></span>
                  </div>

                  <div className="organized-by">
                    <span>ORGANIZED BY</span>
                  </div>

                  <div className="university-info">
                    <h3>FACULTY OF PHARMACY,</h3>
                    <h3>UNITED UNIVERSITY</h3>
                    <h3>PRAYAGRAJ</h3>
                  </div>
                </>
              )}

              <div className="slide-description">
                <p>{slide.content}</p>
              </div>

              <div className="slide-theme">
                <p className="theme-text">"{slide.theme}"</p>
                {slide.id === 1 && <p className="conference-code">FIHTNFPS - 2025</p>}
              </div>

              {slide.showCountdown && (
                <div className="countdown">
                  <h3>Conference Countdown</h3>
                  <div className="countdown-timer">
                    <div className="time-unit">
                      <span className="number">{timeLeft.days || 0}</span>
