import React, { useState, useEffect } from 'react';
import './Home.css';

const Home = () => {
  const [timeLeft, setTimeLeft] = useState({});
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      title: "PHARMORA - 1.0",
      subtitle: "NATIONAL CONFERENCE",
      description: "Fostering Innovation for a Healthier Tomorrow: Navigating the Future of Pharmaceutical Sciences",
      theme: "FIHTNFPS - 2025",
      date: "2025",
      organizer: "FACULTY OF PHARMACY, UNITED UNIVERSITY PRAYAGRAJ"
    },
    {
      title: "UNITED UNIVERSITY",
      subtitle: "EXCELLENCE IN EDUCATION",
      description: "A dynamic and emerging multidisciplinary institution, established under the Private University Act and duly approved by the State Government",
      theme: "Promoting Excellence in Education, Research, and Training",
      date: "2025",
      organizer: "ARTS, SCIENCES, COMMERCE, ENGINEERING, MANAGEMENT, AGRICULTURE"
    },
    {
      title: "PRAYAGRAJ",
      subtitle: "CITY OF PRIME MINISTERS",
      description: "One of India's most ancient and sacred cities, known as Sangam City, famous for hosting the world's largest religious gathering - Kumbh Mela",
      theme: "Rich Heritage, Magnificent Architecture, Spiritual Atmosphere",
      date: "2025",
      organizer: "CONFLUENCE OF GANGA, YAMUNA & SARASWATI"
    },
    {
      title: "FACULTY OF PHARMACY",
      subtitle: "ESTABLISHED 2023",
      description: "Proudly offers Bachelor of Pharmacy (B.Pharm) and Diploma in Pharmacy (D.Pharm) programs with annual intakes of 100 and 60 students respectively",
      theme: "Bridging Industry and Academia Through Cutting-edge Education",
      date: "2025",
      organizer: "STATE-OF-THE-ART LABORATORIES & MODERN INSTRUMENTATION"
    }
  ];

  useEffect(() => {
    const targetDate = new Date('2025-11-07T00:00:00').getTime();

    const timer = setInterval(() => {
      const now = new Date().getTime();
      const distance = targetDate - now;

      const days = Math.floor(distance / (1000 * 60 * 60 * 24));
      const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((distance % (1000 * 60)) / 1000);

      if (distance > 0) {
        setTimeLeft({ days, hours, minutes, seconds });
      } else {
        setTimeLeft({ days: 0, hours: 0, minutes: 0, seconds: 0 });
        clearInterval(timer);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide(prev => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(slideTimer);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide(prev => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide(prev => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section id="home" className="home">
      <div className="slideshow">
        <div className="slides">
          {slides.map((slide, index) => (
            <div
              key={index}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
            >
              <div className="slide-content">
                <div className="slide-header">
                  <h1 className="pharmora-title">
                    {index === 0 ? (
                      <span>
                        <span className="u-red">U</span>
                        <span className="pharmora-blue">{slide.title}</span>
                      </span>
                    ) : (
                      <span className="pharmora-blue">{slide.title}</span>
                    )}
                  </h1>
                  <div className="title-underline"></div>
                </div>

                <div className="slide-subtitle national-conference">{slide.subtitle}</div>

                <div className="year-divider">
                  <div className="year-line orange"></div>
                  <div className="year-text purple">{slide.date}</div>
                  <div className="year-line orange"></div>
                </div>

                <div className="organized-by">ORGANIZED BY</div>

                <div className="university-info">
                  <h3 className="faculty-text">{slide.organizer}</h3>
                </div>

                <div className="slide-description">
                  <p className="theme-quote">"{slide.description}"</p>
                </div>

                <div className="theme-section">
                  <div className="conference-code green">{slide.theme}</div>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="slide-controls">
          <button className="slide-btn" onClick={prevSlide}>‹</button>
          <button className="slide-btn" onClick={nextSlide}>›</button>
        </div>

        <div className="slide-indicators">
          {slides.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Home;
