import React, { useState, useEffect, useRef } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import './App.css';

function App() {
  const [currentSection, setCurrentSection] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(false);
  const [touchStart, setTouchStart] = useState(null);
  const [touchEnd, setTouchEnd] = useState(null);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const canvasRef = useRef(null);
  const particlesRef = useRef([]);

  const sections = [
    {
      id: 'home',
      title: 'PHARMORA - 1.0',
      subtitle: 'NATIONAL CONFERENCE',
      content: {
        description: "Fostering Innovation for a Healthier Tomorrow: Navigating the Future of Pharmaceutical Sciences",
        theme: "FIHTNFPS - 2025",
        organizer: "FACULTY OF PHARMACY, UNITED UNIVERSITY PRAYAGRAJ",
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'about-university',
      title: 'UNITED UNIVERSITY',
      subtitle: 'EXCELLENCE IN EDUCATION',
      content: {
        description: "A dynamic and emerging multidisciplinary institution, established under the Private University Act and duly approved by the State Government",
        details: [
          "Founded with a vision to promote excellence in education, research, and training",
          "Wide range of academic disciplines including Arts, Sciences, Commerce, Engineering",
          "Committed to academic innovation and holistic development",
          "Strong emphasis on quality education and industry relevance"
        ],
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'prayagraj',
      title: 'PRAYAGRAJ',
      subtitle: 'CITY OF PRIME MINISTERS',
      content: {
        description: "One of India's most ancient and sacred cities, known as Sangam City, famous for hosting the world's largest religious gathering - Kumbh Mela",
        details: [
          "Rich cultural heritage spanning thousands of years",
          "Confluence of three sacred rivers: Ganga, Yamuna & Saraswati",
          "Home to prestigious educational institutions",
          "Historic significance in India's freedom struggle"
        ],
        backgroundImage: "/images/chandrashekhar azad.png"
      }
    },
    {
      id: 'key-areas',
      title: 'KEY RESEARCH AREAS',
      subtitle: 'CONFERENCE THEMES',
      content: {
        description: "Exploring cutting-edge pharmaceutical research and innovation",
        areas: [
          { title: "Drug Discovery & Development", icon: "🧬" },
          { title: "Nanotechnology in Pharmaceuticals", icon: "⚛️" },
          { title: "Personalized Medicine", icon: "🎯" },
          { title: "AI in Drug Research", icon: "🤖" },
          { title: "Regulatory Science", icon: "📋" },
          { title: "Phytopharmaceuticals", icon: "🌿" }
        ],
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'dates',
      title: 'IMPORTANT DATES',
      subtitle: 'CONFERENCE TIMELINE',
      content: {
        description: "Mark your calendar for these crucial dates",
        dates: [
          { event: "Abstract Submission Deadline", date: "15th October 2025", status: "upcoming" },
          { event: "Early Bird Registration", date: "20th October 2025", status: "upcoming" },
          { event: "Final Registration", date: "1st November 2025", status: "upcoming" },
          { event: "Conference Dates", date: "7-8 November 2025", status: "main" }
        ],
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'papers',
      title: 'CALL FOR PAPERS',
      subtitle: 'SUBMIT YOUR RESEARCH',
      content: {
        description: "Share your innovative research with the pharmaceutical community",
        categories: [
          { type: "Original Research Papers", description: "Novel findings and methodologies" },
          { type: "Review Articles", description: "Comprehensive literature reviews" },
          { type: "Case Studies", description: "Real-world applications and outcomes" },
          { type: "Short Communications", description: "Brief reports of significant findings" },
          { type: "Poster Presentations", description: "Visual research presentations" }
        ],
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'registration',
      title: 'REGISTRATION',
      subtitle: 'JOIN THE CONFERENCE',
      content: {
        description: "Register now to be part of this prestigious pharmaceutical conference",
        fees: [
          { category: "Students", fee: "₹2,000", earlyBird: "₹1,500", includes: "Conference kit, meals, certificate" },
          { category: "Academics", fee: "₹4,000", earlyBird: "₹3,000", includes: "All materials, networking sessions" },
          { category: "Industry", fee: "₹6,000", earlyBird: "₹4,500", includes: "Premium access, exhibition" },
          { category: "International", fee: "$150", earlyBird: "$120", includes: "Full conference access" }
        ],
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'venue',
      title: 'VENUE',
      subtitle: 'UNITED UNIVERSITY PRAYAGRAJ',
      content: {
        description: "State-of-the-art facilities for an exceptional conference experience",
        facilities: [
          { name: "Modern Auditorium", description: "Advanced AV systems and comfortable seating" },
          { name: "High-Speed Internet", description: "Seamless connectivity throughout campus" },
          { name: "Accommodation", description: "Comfortable nearby hotels and guest houses" },
          { name: "Transportation", description: "Easy accessibility from major transport hubs" }
        ],
        address: "Rawatpur, Jhalwa, Prayagraj, Uttar Pradesh",
        backgroundImage: "/images/uu1.png"
      }
    },
    {
      id: 'speakers',
      title: 'KEYNOTE SPEAKERS',
      subtitle: 'DISTINGUISHED EXPERTS',
      content: {
        description: "Learn from renowned experts in pharmaceutical sciences",
        speakers: [
          { category: "Leading Pharmaceutical Researchers", description: "Pioneers in drug discovery" },
          { category: "Industry Innovators", description: "Visionaries shaping the future" },
          { category: "Regulatory Affairs Experts", description: "Policy and compliance leaders" },
          { category: "Academic Thought Leaders", description: "Educational excellence champions" }
        ],
        backgroundImage: "/images/uu1.png"
      }
    }
  ];

  // Auto-play functionality
  useEffect(() => {
    let interval;
    if (isAutoPlay) {
      interval = setInterval(() => {
        setCurrentSection(prev => (prev + 1) % sections.length);
      }, 8000);
    }
    return () => clearInterval(interval);
  }, [isAutoPlay, sections.length]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'ArrowRight' || e.key === ' ') {
        nextSection();
      } else if (e.key === 'ArrowLeft') {
        prevSection();
      } else if (e.key === 'Escape') {
        setIsAutoPlay(false);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  // Touch navigation
  const handleTouchStart = (e) => {
    setTouchEnd(null);
    setTouchStart(e.targetTouches[0].clientX);
  };

  const handleTouchMove = (e) => {
    setTouchEnd(e.targetTouches[0].clientX);
  };

  const handleTouchEnd = () => {
    if (!touchStart || !touchEnd) return;
    const distance = touchStart - touchEnd;
    const isLeftSwipe = distance > 50;
    const isRightSwipe = distance < -50;

    if (isLeftSwipe) nextSection();
    if (isRightSwipe) prevSection();
  };

  // Particle animation system
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const particles = particlesRef.current;

    // Initialize particles
    const initParticles = () => {
      particles.length = 0;
      for (let i = 0; i < 50; i++) {
        particles.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          vx: (Math.random() - 0.5) * 0.5,
          vy: (Math.random() - 0.5) * 0.5,
          size: Math.random() * 2 + 1,
          opacity: Math.random() * 0.5 + 0.2,
          color: `hsl(${200 + Math.random() * 60}, 70%, 60%)`
        });
      }
    };

    // Animate particles
    const animateParticles = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach(particle => {
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Bounce off edges
        if (particle.x < 0 || particle.x > canvas.width) particle.vx *= -1;
        if (particle.y < 0 || particle.y > canvas.height) particle.vy *= -1;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = particle.color;
        ctx.globalAlpha = particle.opacity;
        ctx.fill();
      });

      ctx.globalAlpha = 1;
      requestAnimationFrame(animateParticles);
    };

    // Resize canvas
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      initParticles();
    };

    resizeCanvas();
    animateParticles();

    window.addEventListener('resize', resizeCanvas);
    return () => window.removeEventListener('resize', resizeCanvas);
  }, []);

  // Mouse tracking for interactive effects
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const nextSection = () => {
    if (isTransitioning) return;
    setIsTransitioning(true);
    setCurrentSection(prev => (prev + 1) % sections.length);
    setTimeout(() => setIsTransitioning(false), 800);
  };

  const prevSection = () => {
    if (isTransitioning) return;
    setIsTransitioning(true);
    setCurrentSection(prev => (prev - 1 + sections.length) % sections.length);
    setTimeout(() => setIsTransitioning(false), 800);
  };

  const goToSection = (index) => {
    if (isTransitioning || index === currentSection) return;
    setIsTransitioning(true);
    setCurrentSection(index);
    setTimeout(() => setIsTransitioning(false), 800);
  };

  const toggleAutoPlay = () => {
    setIsAutoPlay(!isAutoPlay);
  };

  const renderSectionContent = (section, index) => {
    const { content } = section;

    switch (section.id) {
      case 'home':
        return (
          <div className="slide-content home-slide">
            <div className="floating-elements">
              <div className="float-element" style={{ '--delay': '0s' }}>💊</div>
              <div className="float-element" style={{ '--delay': '2s' }}>🧬</div>
              <div className="float-element" style={{ '--delay': '4s' }}>⚗️</div>
              <div className="float-element" style={{ '--delay': '6s' }}>🔬</div>
            </div>
            <div className="slide-header">
              <h1 className="pharmora-title animated-title">
                <span className="u-red">U</span>
                <span className="pharmora-blue">PHARMORA - 1.0</span>
              </h1>
              <div className="title-underline animated-underline"></div>
            </div>
            <div className="conference-layout">
              <div className="slide-subtitle national-conference pulse-animation">{section.subtitle}</div>
              <div className="year-divider">
                <div className="year-line orange animated-line"></div>
                <div className="year-text purple glow-text">2025</div>
                <div className="year-line orange animated-line"></div>
              </div>
              <div className="organized-by-box hover-lift">
                <div className="organized-by-text">ORGANIZED BY</div>
              </div>
              <div className="university-info">
                <h3 className="faculty-text typewriter-effect">{content.organizer}</h3>
              </div>
              <div className="slide-description">
                <p className="theme-quote fade-in-up">"{content.description}"</p>
              </div>
              <div className="theme-section">
                <div className="conference-code green shimmer-effect">{content.theme}</div>
              </div>
            </div>
          </div>
        );

      case 'key-areas':
        return (
          <div className="slide-content areas-slide">
            <h1 className="section-title wave-animation">{section.title}</h1>
            <h2 className="section-subtitle fade-in">{section.subtitle}</h2>
            <p className="section-description slide-in-left">{content.description}</p>
            <div className="areas-grid">
              {content.areas.map((area, idx) => (
                <div key={idx} className="area-card interactive-card"
                  style={{ animationDelay: `${idx * 0.1}s` }}
                  onMouseEnter={(e) => e.target.style.transform = 'scale(1.05) rotateY(5deg)'}
                  onMouseLeave={(e) => e.target.style.transform = 'scale(1) rotateY(0deg)'}>
                  <div className="area-icon bounce-icon">{area.icon}</div>
                  <h3 className="area-title">{area.title}</h3>
                  <div className="card-glow"></div>
                </div>
              ))}
            </div>
          </div>
        );

      case 'dates':
        return (
          <div className="slide-content dates-slide">
            <h1 className="section-title">{section.title}</h1>
            <h2 className="section-subtitle">{section.subtitle}</h2>
            <p className="section-description">{content.description}</p>
            <div className="dates-timeline">
              {content.dates.map((item, idx) => (
                <div key={idx} className={`date-item ${item.status}`} style={{ animationDelay: `${idx * 0.2}s` }}>
                  <div className="date-marker"></div>
                  <div className="date-content">
                    <h3>{item.event}</h3>
                    <p>{item.date}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      case 'registration':
        return (
          <div className="slide-content registration-slide">
            <h1 className="section-title">{section.title}</h1>
            <h2 className="section-subtitle">{section.subtitle}</h2>
            <p className="section-description">{content.description}</p>
            <div className="registration-grid">
              {content.fees.map((fee, idx) => (
                <div key={idx} className="fee-card" style={{ animationDelay: `${idx * 0.1}s` }}>
                  <h3>{fee.category}</h3>
                  <div className="fee-details">
                    <p className="regular-fee">Regular: {fee.fee}</p>
                    <p className="early-bird">Early Bird: {fee.earlyBird}</p>
                    <p className="includes">{fee.includes}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return (
          <div className="slide-content default-slide">
            <h1 className="section-title">{section.title}</h1>
            <h2 className="section-subtitle">{section.subtitle}</h2>
            <p className="section-description">{content.description}</p>

            {content.details && (
              <ul className="details-list">
                {content.details.map((detail, idx) => (
                  <li key={idx} style={{ animationDelay: `${idx * 0.1}s` }}>{detail}</li>
                ))}
              </ul>
            )}

            {content.categories && (
              <div className="categories-grid">
                {content.categories.map((cat, idx) => (
                  <div key={idx} className="category-card" style={{ animationDelay: `${idx * 0.1}s` }}>
                    <h4>{cat.type}</h4>
                    <p>{cat.description}</p>
                  </div>
                ))}
              </div>
            )}

            {content.facilities && (
              <div className="facilities-grid">
                {content.facilities.map((facility, idx) => (
                  <div key={idx} className="facility-card" style={{ animationDelay: `${idx * 0.1}s` }}>
                    <h4>{facility.name}</h4>
                    <p>{facility.description}</p>
                  </div>
                ))}
              </div>
            )}

            {content.speakers && (
              <div className="speakers-grid">
                {content.speakers.map((speaker, idx) => (
                  <div key={idx} className="speaker-card" style={{ animationDelay: `${idx * 0.1}s` }}>
                    <h4>{speaker.category}</h4>
                    <p>{speaker.description}</p>
                  </div>
                ))}
              </div>
            )}

            {content.address && (
              <div className="address-section">
                <h3>Address:</h3>
                <p>{content.address}</p>
              </div>
            )}
          </div>
        );
    }
  };

  return (
    <div className="App slideshow-app">
      <canvas ref={canvasRef} className="particle-canvas"></canvas>

      <div
        className="cursor-follower"
        style={{
          left: mousePosition.x - 10,
          top: mousePosition.y - 10,
        }}
      ></div>

      <Header />

      <main
        className={`main-slideshow ${isTransitioning ? 'transitioning' : ''}`}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
      >
        <div className="slideshow-container">
          {sections.map((section, index) => (
            <div
              key={section.id}
              className={`main-slide ${index === currentSection ? 'active' : ''} ${section.id}-slide slide-${index}`}
              style={{
                backgroundImage: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.6)), url('${section.content.backgroundImage}')`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat',
                transform: index === currentSection ? 'scale(1)' : 'scale(0.95)',
                filter: index === currentSection ? 'brightness(1)' : 'brightness(0.7)'
              }}
            >
              <div className="slide-overlay"></div>
              {renderSectionContent(section, index)}
            </div>
          ))}
        </div>

        <div className="slideshow-controls enhanced-controls">
          <button className="control-btn prev-btn ripple-effect" onClick={prevSection} title="Previous slide">
            <span className="btn-icon">‹</span>
            <span className="btn-ripple"></span>
          </button>
          <button className="control-btn next-btn ripple-effect" onClick={nextSection} title="Next slide">
            <span className="btn-icon">›</span>
            <span className="btn-ripple"></span>
          </button>
        </div>

        <button
          className={`control-btn autoplay-btn enhanced-autoplay ${isAutoPlay ? 'active' : ''}`}
          onClick={toggleAutoPlay}
          title={isAutoPlay ? 'Pause slideshow' : 'Play slideshow'}
        >
          <span className="autoplay-icon">{isAutoPlay ? '⏸' : '▶'}</span>
          <div className="autoplay-ring"></div>
        </button>

        <div className="section-indicators enhanced-indicators">
          {sections.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSection ? 'active' : ''}`}
              onClick={() => goToSection(index)}
              title={sections[index].title}
            >
              <span className="indicator-dot"></span>
              <span className="indicator-label">{sections[index].title}</span>
            </button>
          ))}
        </div>

        <div className="section-navigation enhanced-nav">
          <div className="nav-menu">
            {sections.map((section, index) => (
              <button
                key={section.id}
                className={`nav-item ${index === currentSection ? 'active' : ''}`}
                onClick={() => goToSection(index)}
              >
                <span className="nav-text">{section.title}</span>
                <div className="nav-underline"></div>
              </button>
            ))}
          </div>
        </div>

        <div className="progress-bar enhanced-progress">
          <div
            className="progress-fill"
            style={{ width: `${((currentSection + 1) / sections.length) * 100}%` }}
          ></div>
          <div className="progress-glow"></div>
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default App;
