import React, { useState, useEffect } from 'react';
import './Home.css';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [currentUUSlide, setCurrentUUSlide] = useState(0);
  const [countdown, setCountdown] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  // Conference date: November 7, 2025
  const conferenceDate = new Date('2025-11-07T09:00:00').getTime();

  const slides = [
    {
      title: "PHARMORA - 1.0",
      subtitle: "NATIONAL CONFERENCE",
      description: "Fostering Innovation for a Healthier Tomorrow: Navigating the Future of Pharmaceutical Sciences",
      theme: "FIHTNFPS - 2025",
      date: "2025",
      organizer: "FACULTY OF PHARMACY, UNITED UNIVERSITY PRAYAGRAJ",
      backgroundImage: "/images/pharmora-1.0.png",
      backgroundColor: "#3498db",
      textColor: "#ffffff"
    },
    {
      title: "UNITED UNIVERSITY",
      subtitle: "EXCELLENCE IN EDUCATION",
      description: "A dynamic and emerging multidisciplinary institution, established under the Private University Act and duly approved by the State Government",
      theme: "Promoting Excellence in Education, Research, and Training",
      date: "2025",
      organizer: "ARTS, SCIENCES, COMMERCE, ENGINEERING, MANAGEMENT, AGRICULTURE",
      backgroundImage: "/images/uu1.png"
    },
    {
      title: "PRAYAGRAJ",
      subtitle: "CITY OF PRIME MINISTERS",
      description: "One of India's most ancient and sacred cities, known as Sangam City, famous for hosting the world's largest religious gathering - Kumbh Mela",
      theme: "Rich Heritage, Magnificent Architecture, Spiritual Atmosphere",
      date: "2025",
      organizer: "CONFLUENCE OF GANGA, YAMUNA & SARASWATI",
      backgroundImage: "/images/chandrashekhar azad.png"
    },
    {
      title: "FUTURE OF PHARMACY",
      subtitle: "INNOVATION & TECHNOLOGY",
      description: "Exploring cutting-edge pharmaceutical technologies, AI in drug discovery, personalized medicine, and the evolving role of pharmacists in healthcare",
      theme: "Advancing Pharmaceutical Sciences Through Technology",
      date: "2025",
      organizer: "NEXT GENERATION PHARMACEUTICAL PROFESSIONALS",
      backgroundImage: "/images/uu1.png"
    }
  ];

  const universityImages = [
    "/images/uu1.png",
    "/images/uu2.png",
    "/images/uu3.png",
    "/images/uu4.png",
    "/images/uu5.png",
    "/images/uu6.png"
  ];

  // Countdown timer effect
  useEffect(() => {
    const updateCountdown = () => {
      const now = new Date().getTime();
      const distance = conferenceDate - now;

      if (distance > 0) {
        const days = Math.floor(distance / (1000 * 60 * 60 * 24));
        const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((distance % (1000 * 60)) / 1000);

        setCountdown({ days, hours, minutes, seconds });
      } else {
        setCountdown({ days: 0, hours: 0, minutes: 0, seconds: 0 });
      }
    };

    updateCountdown();
    const countdownTimer = setInterval(updateCountdown, 1000);

    return () => clearInterval(countdownTimer);
  }, [conferenceDate]);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide(prev => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(slideTimer);
  }, [slides.length]);

  useEffect(() => {
    const uuSlideTimer = setInterval(() => {
      setCurrentUUSlide(prev => (prev + 1) % universityImages.length);
    }, 3000);

    return () => clearInterval(uuSlideTimer);
  }, [universityImages.length]);

  const nextSlide = () => {
    setCurrentSlide(prev => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide(prev => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section id="home" className="home">
      <div className="slideshow">
        <div className="slides">
          {slides.map((slide, index) => (
            <div
              key={index}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
              style={{
                backgroundImage: slide.backgroundImage ? `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url('${slide.backgroundImage}')` : slide.backgroundColor,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat',
                color: slide.textColor
              }}
            >
              <div className="slide-content">
                <div className="slide-header">
                  <h1 className="pharmora-title">
                    {index === 0 ? (
                      <span>
                        <span className="u-red">U</span>
                        <span className="pharmora-blue">{slide.title}</span>
                      </span>
                    ) : (
                      <span className="pharmora-blue">{slide.title}</span>
                    )}
                  </h1>
                  <div className="title-underline"></div>
                </div>

                <div className="slide-subtitle national-conference">{slide.subtitle}</div>

                <div className="year-divider">
                  <div className="year-line orange"></div>
                  <div className="year-text purple">{slide.date}</div>
                  <div className="year-line orange"></div>
                </div>

                <div className="organized-by">ORGANIZED BY</div>

                <div className="university-info">
                  <h3 className="faculty-text">{slide.organizer}</h3>
                </div>

                <div className="slide-description">
                  <p className="theme-quote">"{slide.description}"</p>
                </div>

                <div className="theme-section">
                  <div className="conference-code green">{slide.theme}</div>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="slide-controls">
          <button className="slide-btn" onClick={prevSlide}>‹</button>
          <button className="slide-btn" onClick={nextSlide}>›</button>
        </div>

        <div className="slide-indicators">
          {slides.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>

      {/* University Welcome Section */}
      <div className="university-welcome-section">
        <div className="welcome-container">
          <div className="welcome-content">
            <h1 className="welcome-title">
              <span className="welcome-text">WELCOME TO</span>
              <span className="university-name">UNITED UNIVERSITY PRAYAGRAJ</span>
            </h1>

            <div className="legacy-content">
              <h2 className="legacy-title">A Legacy of Excellence & Innovation</h2>
              <p className="legacy-description">
                For decades, the <strong>United Group of Institutions</strong> has stood as a beacon of educational excellence,
                nurturing minds and shaping futures across diverse academic disciplines. From humble beginnings to becoming
                a prestigious multidisciplinary university, our journey reflects unwavering commitment to quality education,
                research innovation, and holistic development.
              </p>
              <p className="legacy-description">
                Our legacy is built on the foundation of <em>academic integrity, industry relevance, and social responsibility</em>.
                We have consistently evolved with changing times, embracing cutting-edge technologies and pedagogical approaches
                while maintaining our core values of excellence, ethics, and empowerment. Today, United University stands proud
                as a dynamic institution that bridges the gap between traditional wisdom and contemporary innovation.
              </p>
              <div className="legacy-highlights">
                <div className="highlight-item">
                  <span className="highlight-number">25+</span>
                  <span className="highlight-text">Years of Excellence</span>
                </div>
                <div className="highlight-item">
                  <span className="highlight-number">10,000+</span>
                  <span className="highlight-text">Alumni Worldwide</span>
                </div>
                <div className="highlight-item">
                  <span className="highlight-number">50+</span>
                  <span className="highlight-text">Academic Programs</span>
                </div>
              </div>
            </div>
          </div>

          <div className="university-slideshow">
            <div className="uu-slides">
              {universityImages.map((image, index) => (
                <div
                  key={index}
                  className={`uu-slide ${index === currentUUSlide ? 'active' : ''}`}
                >
                  <img src={image} alt={`United University ${index + 1}`} />
                </div>
              ))}
            </div>
            <div className="uu-slide-indicators">
              {universityImages.map((_, index) => (
                <div
                  key={index}
                  className={`uu-indicator ${index === currentUUSlide ? 'active' : ''}`}
                />
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="about-sections">
        <div className="about-section">
          <h3>ABOUT UNITED UNIVERSITY</h3>
          <p>
            United University is a dynamic and emerging multidisciplinary institution, established
            under the <em>Private University Act</em> and duly approved by the State Government. The
            university has been founded with a vision to promote excellence in education, research,
            and training across a wide range of academic disciplines including Arts, Sciences,
            Commerce, Engineering, Management, Agriculture, and Mass Communication.
          </p>
          <p>
            Committed to academic innovation and holistic development, United University strives to
            be at the forefront of learning, teaching, and research. With a strong emphasis on quality
            education and industry relevance, the university aims to nurture skilled professionals who
            will contribute meaningfully to national growth and global progress.
          </p>
        </div>

        <div className="about-section">
          <h3>ABOUT FACULTY OF PHARMACY</h3>
          <p>
            Established in <strong>2023</strong>, the <strong>Faculty of Pharmacy at United University</strong> proudly offers
            <strong>Bachelor of Pharmacy (B.Pharm)</strong> and <strong>Diploma in Pharmacy (D.Pharm)</strong> programs with
            annual intakes of <strong>100</strong> and <strong>60</strong> students, respectively. These programs are meticulously
            designed to foster academic excellence, practical proficiency, and industry readiness.
          </p>
          <p>
            Our graduates are well-prepared for dynamic career paths across <strong>pharmaceutical
              manufacturing, quality assurance, marketing, regulatory affairs, research and
              development, healthcare institutions</strong>, and prestigious government sectors such as the
            <strong>Food and Drug Administration</strong>. With the growing demand for skilled pharmacy
            professionals, our mission is to bridge the gap between industry and academia through
            cutting-edge education and hands-on training.
          </p>
          <p>
            The Faculty is equipped with <strong>state-of-the-art laboratories, in the well Pharmacy
              building of United University (PCL) guidelines, featuring modern instrumentation,
              advanced simulation software, and digital learning tools</strong>. This infrastructure ensures
            our students gain real-time industry exposure within an academic setting. Our biggest
            strength lies in our highly qualified, dedicated faculty and staff, who are passionate
            about mentoring students and nurturing the next generation of pharmacy professionals.
          </p>
          <p>
            Recognized for our academic integrity and industry connections, we are dedicated to
            developing <strong>competent, ethical, and innovative leaders</strong> in the field of pharmaceutical sciences.
          </p>
        </div>

        <div className="about-section">
          <h3>ABOUT THE CONFERENCE</h3>
          <p className="conference-subtitle">
            <strong>FIHTNFPS - 2025</strong>
          </p>
          <p>
            Aims to bring together leading academicians, researchers, healthcare professionals and
            industry experts to explore transformative ideas and technological advancements shaping
            the future of pharmaceutical sciences. In an era marked by rapid scientific progress and
            global health challenges, the pharmaceutical sector stands at a crucial intersection of
            innovation and responsibility.
          </p>
          <p>
            This conference provides a dynamic platform to share cutting-edge research, foster
            interdisciplinary collaborations, and promote sustainable solutions that enhance healthcare
            delivery and drug development. Key themes will include advancements in <strong>drug discovery
              and delivery, regulatory science, nanotechnology, personalized medicine, artificial
              intelligence in pharma, phytopharmaceuticals</strong>, and the evolving role of pharmacists
            in public health.
          </p>
          <p>
            Special focus will also be placed on fostering entrepreneurship, start-up ecosystems, and
            global partnerships that accelerate translational research from bench to bedside. Through
            keynote addresses, plenary sessions, panel discussions, poster presentations, and networking
            forums, participants will gain valuable insights into emerging trends and technologies that
            are revolutionizing the pharmaceutical landscape.
          </p>
          <p>
            <em>Join us in this collaborative journey as we navigate the future of pharmaceutical
              sciences and work towards fostering innovation for a healthier tomorrow.</em>
          </p>
        </div>
      </div>

      <div className="countdown-section">
        <h2>Countdown to the Conference</h2>
        <div className="countdown-timer">
          <div className="countdown-item">
            <span className="countdown-number">{countdown.days}</span>
            <span className="countdown-text">Days</span>
          </div>
          <div className="countdown-item">
            <span className="countdown-number">{countdown.hours}</span>
            <span className="countdown-text">Hours</span>
          </div>
          <div className="countdown-item">
            <span className="countdown-number">{countdown.minutes}</span>
            <span className="countdown-text">Minutes</span>
          </div>
          <div className="countdown-item">
            <span className="countdown-number">{countdown.seconds}</span>
            <span className="countdown-text">Seconds</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Home;
