import React, { useState, useEffect } from 'react';
import './Home.css';

const Home = () => {
  const [timeLeft, setTimeLeft] = useState({});
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      title: "United University Conference 2025",
      subtitle: "PHARMORA 2025",
      description: "Join us for an inspiring gathering of minds, innovation, and academic excellence",
      theme: "Innovation in Education & Technology",
      date: "March 15, 2025"
    },
    {
      title: "International Research Summit",
      subtitle: "RESEARCH EXCELLENCE",
      description: "Discover groundbreaking research and connect with leading academics from around the world",
      theme: "Advancing Knowledge Through Collaboration",
      date: "March 15, 2025"
    },
    {
      title: "Future of Education",
      subtitle: "DIGITAL TRANSFORMATION",
      description: "Explore the latest trends in educational technology and digital learning methodologies",
      theme: "Transforming Education for Tomorrow",
      date: "March 15, 2025"
    }
  ];

  useEffect(() => {
    const conferenceDate = new Date('2025-03-15T09:00:00').getTime();

    const timer = setInterval(() => {
      const now = new Date().getTime();
      const difference = conferenceDate - now;

      if (difference > 0) {
        setTimeLeft({
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((difference % (1000 * 60)) / 1000)
        });
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide(prev => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(slideTimer);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide(prev => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide(prev => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section id="home" className="home">
      <div className="slideshow-container">
        {slides.map((slide, index) => (
          <div key={index} className={`slide ${index === currentSlide ? 'active' : ''}`}>
            <div className="slide-content">
              <div className="slide-header">
                <h1>
                  <span className="u-red">U</span>nited <span className="u-red">U</span>niversity
                </h1>
                <div className="title-underline"></div>
              </div>

              <div className="slide-subtitle pharmora-blue">{slide.subtitle}</div>

              <div className="year-divider">
                <div className="year-line"></div>
                <div className="year-text">2025</div>
                <div className="year-line"></div>
              </div>

              <div className="organized-by">Organized by United University</div>

              <div className="university-info">
                <h3>UNITED UNIVERSITY</h3>
                <h3>PRAYAGRAJ</h3>
              </div>

              <div className="slide-description">
                <p>{slide.description}</p>
              </div>

              <div className="theme-section">
                <div className="theme-text">"{slide.theme}"</div>
                <div className="conference-code">UU-CONF-2025</div>
              </div>

              <div className="conference-details">
                <p><strong>Date:</strong> {slide.date}</p>
              </div>
            </div>
          </div>
        ))}

        <div className="slide-controls">
          <button className="slide-btn" onClick={prevSlide}>‹</button>
          <button className="slide-btn" onClick={nextSlide}>›</button>
        </div>

        <div className="slide-indicators">
          {slides.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>

      <div className="countdown">
        <h3>Conference Countdown</h3>
        <div className="countdown-timer">
          <div className="time-unit">
            <span className="number">{timeLeft.days || 0}</span>
            <span className="label">Days</span>
          </div>
          <div className="time-unit">
            <span className="number">{timeLeft.hours || 0}</span>
            <span className="label">Hours</span>
          </div>
          <div className="time-unit">
            <span className="number">{timeLeft.minutes || 0}</span>
            <span className="label">Minutes</span>
          </div>
          <div className="time-unit">
            <span className="number">{timeLeft.seconds || 0}</span>
            <span className="label">Seconds</span>
          </div>
        </div>
      </div>

      <button className="btn cta-btn">Register Now</button>

      <div className="about-sections">
        <div className="about-section">
          <h3>ABOUT THE CONFERENCE</h3>
          <p className="conference-subtitle">
            A Premier Academic Gathering for Innovation and Excellence
          </p>
          <p>
            The United University Conference 2025 brings together leading researchers, academicians,
            industry experts, and students from around the world. This prestigious event serves as
            a platform for sharing cutting-edge research, fostering collaboration, and exploring
            innovative solutions to contemporary challenges in education and technology.
          </p>
          <p>
            <em>Join us for three days of inspiring presentations, interactive workshops, and
              networking opportunities that will shape the future of academic excellence.</em>
          </p>
        </div>

        <div className="about-section">
          <h3>CONFERENCE HIGHLIGHTS</h3>
          <p>
            • <strong>Keynote Sessions</strong> by internationally renowned speakers<br />
            • <strong>Research Paper Presentations</strong> across multiple disciplines<br />
            • <strong>Interactive Workshops</strong> on emerging technologies<br />
            • <strong>Networking Sessions</strong> with industry leaders<br />
            • <strong>Student Research Competitions</strong> with attractive prizes<br />
            • <strong>Cultural Programs</strong> showcasing local heritage
          </p>
        </div>

        <div className="about-section">
          <h3>WHO SHOULD ATTEND</h3>
          <p>
            This conference is designed for researchers, faculty members, graduate students,
            industry professionals, and anyone passionate about advancing knowledge and innovation.
            Whether you're presenting your research, seeking collaboration opportunities, or
            simply looking to stay updated with the latest developments in your field,
            this conference offers valuable insights and connections.
          </p>
        </div>
      </div>
