import React, { useState, useEffect, useRef } from 'react';
import './Home.css';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [currentUUSlide, setCurrentUUSlide] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [isUUPaused, setIsUUPaused] = useState(false);
  const [touchStart, setTouchStart] = useState(null);
  const [touchEnd, setTouchEnd] = useState(null);
  const slideRef = useRef(null);
  const uuSlideRef = useRef(null);

  const slides = [
    {
      title: "PHARMORA - 1.0",
      subtitle: "NATIONAL CONFERENCE",
      description: "Fostering Innovation for a Healthier Tomorrow: Navigating the Future of Pharmaceutical Sciences",
      theme: "FIHTNFPS - 2025",
      date: "2025",
      organizer: "FACULTY OF PHARMACY, UNITED UNIVERSITY PRAYAGRAJ",
      backgroundImage: "/images/uu2.png",
      backgroundColor: "#1a365d",
      textColor: "#ffffff"
    },
    {
      title: "UNITED UNIVERSITY",
      subtitle: "EXCELLENCE IN EDUCATION",
      description: "A dynamic and emerging multidisciplinary institution, established under the Private University Act and duly approved by the State Government",
      theme: "Promoting Excellence in Education, Research, and Training",
      date: "2025",
      organizer: "ARTS, SCIENCES, COMMERCE, ENGINEERING, MANAGEMENT, AGRICULTURE",
      backgroundImage: "/images/uu1.png"
    },
    {
      title: "PRAYAGRAJ",
      subtitle: "CITY OF PRIME MINISTERS",
      description: "One of India's most ancient and sacred cities, known as Sangam City, famous for hosting the world's largest religious gathering - Kumbh Mela",
      theme: "Rich Heritage, Magnificent Architecture, Spiritual Atmosphere",
      date: "2025",
      organizer: "CONFLUENCE OF GANGA, YAMUNA & SARASWATI",
      backgroundImage: "/images/travanisangam.png",
      backgroundColor: "#1a365d"
    },
    {
      title: "FUTURE OF PHARMACY",
      subtitle: "INNOVATION & TECHNOLOGY",
      description: "Exploring cutting-edge pharmaceutical technologies, AI in drug discovery, personalized medicine, and the evolving role of pharmacists in healthcare",
      theme: "Advancing Pharmaceutical Sciences Through Technology",
      date: "2025",
      organizer: "NEXT GENERATION PHARMACEUTICAL PROFESSIONALS",
      backgroundImage: "/images/uu1.png"
    }
  ];

  const universityImages = [
    "/images/uu1.png",
    "/images/uu2.png",
    "/images/uu3.png",
    "/images/uu5.png",
    "/images/uu6.png"
  ];

  // Main slideshow timer with pause functionality
  useEffect(() => {
    if (!isPaused) {
      const slideTimer = setInterval(() => {
        setCurrentSlide(prev => (prev + 1) % slides.length);
      }, 3000);
      return () => clearInterval(slideTimer);
    }
  }, [slides.length, isPaused]);

  // University slideshow timer with pause functionality
  useEffect(() => {
    if (!isUUPaused) {
      const uuSlideTimer = setInterval(() => {
        setCurrentUUSlide(prev => (prev + 1) % universityImages.length);
      }, 3000);
      return () => clearInterval(uuSlideTimer);
    }
  }, [universityImages.length, isUUPaused]);

  // Touch handlers for swipe functionality
  const handleTouchStart = (e) => {
    setTouchEnd(null);
    setTouchStart(e.targetTouches[0].clientX);
  };

  const handleTouchMove = (e) => {
    setTouchEnd(e.targetTouches[0].clientX);
  };

  const handleTouchEnd = (slideType = 'main') => {
    if (!touchStart || !touchEnd) return;

    const distance = touchStart - touchEnd;
    const isLeftSwipe = distance > 50;
    const isRightSwipe = distance < -50;

    if (slideType === 'main') {
      if (isLeftSwipe) {
        setCurrentSlide(prev => (prev + 1) % slides.length);
      }
      if (isRightSwipe) {
        setCurrentSlide(prev => (prev - 1 + slides.length) % slides.length);
      }
    } else {
      if (isLeftSwipe) {
        setCurrentUUSlide(prev => (prev + 1) % universityImages.length);
      }
      if (isRightSwipe) {
        setCurrentUUSlide(prev => (prev - 1 + universityImages.length) % universityImages.length);
      }
    }
  };

  // Manual navigation functions
  const nextSlide = () => {
    setCurrentSlide(prev => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide(prev => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const nextUUSlide = () => {
    setCurrentUUSlide(prev => (prev + 1) % universityImages.length);
  };

  const prevUUSlide = () => {
    setCurrentUUSlide(prev => (prev - 1 + universityImages.length) % universityImages.length);
  };

  // Scroll-triggered animations
  useEffect(() => {
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
        }
      });
    }, observerOptions);

    // Observe all sections with animation class
    const animatedElements = document.querySelectorAll('.animate-on-scroll');
    animatedElements.forEach(el => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  return (
    <div className="home-container">
      {/* Welcome Section with Slideshow */}
      <section className="welcome-section">
        <div
          className="slideshow-container"
          onMouseEnter={() => setIsPaused(true)}
          onMouseLeave={() => setIsPaused(false)}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={() => handleTouchEnd('main')}
        >
          {slides.map((slide, index) => (
            <div
              key={index}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
              style={{
                backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url(${slide.backgroundImage})`,
                backgroundColor: slide.backgroundColor || '#3498db'
              }}
            >
              <div className="slide-content">
                <h1 className="slide-title">{slide.title}</h1>
                <h2 className="slide-subtitle">{slide.subtitle}</h2>
                <p className="slide-description">{slide.description}</p>
                <div className="slide-theme">{slide.theme}</div>
                <div className="slide-organizer">{slide.organizer}</div>
              </div>
            </div>
          ))}
          {/* Slide Controls */}
          <div className="slide-controls">
            <button className="slide-btn prev" onClick={prevSlide} aria-label="Previous slide">
              &#8249;
            </button>
            <button className="slide-btn next" onClick={nextSlide} aria-label="Next slide">
              &#8250;
            </button>
          </div>
          {/* Slide Indicators */}
          <div className="slide-indicators">
            {slides.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === currentSlide ? 'active' : ''}`}
                onClick={() => goToSlide(index)}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
          {/* Pause indicator */}
          {isPaused && (
            <div className="pause-indicator">
              <span>⏸️ Paused</span>
            </div>
          )}
        </div>
      </section>

      {/* University Section */}
      <section className="university-section">
        <div className="content-container">
          <h2 className="section-title">Welcome to United University</h2>
          <div className="content-text">
            <p>United University is a dynamic and emerging multidisciplinary institution, established under the Private University Act and duly approved by the State Government.</p>
            <p>The university has been founded with a vision to promote excellence in education, research, and training across a wide range of academic disciplines including Arts, Sciences, Commerce, Engineering, Management, Agriculture, and Mass Communication.</p>
            <p>Committed to academic innovation and holistic development, United University strives to be at the forefront of learning, teaching, and research.</p>
            <p>With a strong emphasis on quality education and industry relevance, the university aims to nurture skilled professionals who will contribute meaningfully to national growth and global progress.</p>
          </div>
          <div
            className="university-slideshow"
            ref={uuSlideRef}
            onMouseEnter={() => setIsUUPaused(true)}
            onMouseLeave={() => setIsUUPaused(false)}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={() => handleTouchEnd('university')}
          >
            {universityImages.map((image, index) => (
              <div
                key={index}
                className={`university-slide ${index === currentUUSlide ? 'active' : ''}`}
                style={{ backgroundImage: `url(${image})` }}
              />
            ))}
            {/* University slide controls */}
            <div className="uu-slide-controls">
              <button className="uu-slide-btn prev" onClick={prevUUSlide} aria-label="Previous university image">
                &#8249;
              </button>
              <button className="uu-slide-btn next" onClick={nextUUSlide} aria-label="Next university image">
                &#8250;
              </button>
            </div>
            {/* University slide indicators */}
            <div className="uu-slide-indicators">
              {universityImages.map((_, index) => (
                <div
                  key={index}
                  className={`uu-indicator ${index === currentUUSlide ? 'active' : ''}`}
                />
              ))}
            </div>
            {/* University pause indicator */}
            {isUUPaused && (
              <div className="uu-pause-indicator">
                <span>⏸️</span>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* Department of Pharmacy Section */}
      <section className="department-section">
        <div className="content-container">
          <h2 className="section-title">Department of Pharmacy</h2>
          <div className="content-text">
            <p>Established in <strong>2023</strong>, the <strong>Faculty of Pharmacy at United University</strong> proudly offers <strong>Bachelor of Pharmacy (B.Pharm)</strong> and <strong>Diploma in Pharmacy (D.Pharm)</strong> programs with annual intakes of <strong>100</strong> and <strong>60</strong> students, respectively.</p>
            <p>These programs are meticulously designed to foster academic excellence, practical proficiency, and industry readiness.</p>
            <p>Our graduates are well-prepared for dynamic career paths across <strong>pharmaceutical manufacturing, quality assurance, marketing, regulatory affairs, research and development, healthcare institutions</strong>, and prestigious government sectors such as the <strong>Food and Drug Administration</strong>.</p>
            <p>With the growing demand for skilled pharmacy professionals, our mission is to bridge the gap between industry and academia through cutting-edge education and hands-on training.</p>
            <p>The Faculty is equipped with <strong>state-of-the-art laboratories, in the well Pharmacy building of United University (PCL) guidelines, featuring modern instrumentation, advanced simulation software, and digital learning tools</strong>.</p>
            <p>This infrastructure ensures our students gain real-time industry exposure within an academic setting.</p>
            <p>Our biggest strength lies in our highly qualified, dedicated faculty and staff, who are passionate about mentoring students and nurturing the next generation of pharmacy professionals.</p>
            <p>Recognized for our academic integrity and industry connections, we are dedicated to developing <strong>competent, ethical, and innovative leaders</strong> in the field of pharmaceutical sciences.</p>
          </div>
        </div>
      </section>

      {/* About Prayagraj Section */}
      <section className="about-prayagraj">
        <div className="content-container">
          <h2 className="section-title">About Prayagraj</h2>
          <div className="content-text">
            <p>Prayagraj, the city of confluence, is a major pilgrimage center and hosts the Kumbh Mela, the world's largest religious gathering.</p>
            <p>One of India's most ancient and sacred cities, Prayagraj is known as Sangam City, famous for its rich heritage, magnificent architecture, and spiritual atmosphere.</p>
            <p>The city is situated at the confluence of the Ganga, Yamuna, and Saraswati rivers, making it a unique and sacred destination for millions of devotees and tourists alike.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
