import React, { useState, useEffect } from 'react';
import './Home.css';


const Home = () => {
  const [timeLeft, setTimeLeft] = useState({});

  useEffect(() => {
    const conferenceDate = new Date('2024-12-31T09:00:00').getTime();

    const timer = setInterval(() => {
      const now = new Date().getTime();
      const difference = conferenceDate - now;

      if (difference > 0) {
        setTimeLeft({
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((difference % (1000 * 60)) / 1000)
        });
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  return (
    <section id="home" className="home">
  {/* <img src={process.env.PUBLIC_URL + '/uu1.png'} alt="Conference" className="hero-image" /> */}
      <div className="hero-content">
        <h1>United University Conference 2024</h1>
        <p>Join us for an inspiring gathering of minds, innovation, and academic excellence</p>
        <div className="conference-details">
          <p><strong>Date:</strong> December 31, 2024</p>
          <p><strong>Theme:</strong> "Innovation in Education & Technology"</p>
        </div>

        <div className="countdown">
          <h3>Conference Countdown</h3>
          <div className="countdown-timer">
            <div className="time-unit">
              <span className="number">{timeLeft.days || 0}</span>
              <span className="label">Days</span>
            </div>
            <div className="time-unit">
              <span className="number">{timeLeft.hours || 0}</span>
              <span className="label">Hours</span>
            </div>
            <div className="time-unit">
              <span className="number">{timeLeft.minutes || 0}</span>
              <span className="label">Minutes</span>
            </div>
            <div className="time-unit">
              <span className="number">{timeLeft.seconds || 0}</span>
              <span className="label">Seconds</span>
            </div>
          </div>
        </div>

        <button className="btn cta-btn">Register Now</button>
      </div>
    </section>
  );
};

export default Home;
